mapper.select:
SELECT idDocumento, idClase, titulo, nombre, path, idArchivo
FROM documento 

	 idClase = #{idClase} 
	 AND anio >=  #{anioIni} 
	 AND anio <= #{anioFin} 
	 AND UPPER(nombre) = UPPER(#{nombre})

SELECT idDocumento, idClase, titulo, nombre, path
FROM documento 
WHERE idDocumento = #{idDocumento}

mapper.update:
UPDATE documento
titulo = #{titulo}, nombre = #{nombre}, path = #{path}, idClase = #{idClase}, anio = #{anio}
WHERE idDocumento = #{idDocumento}
